actionConfiguration.body:
async () => {
  const name = NewTodoInput.text;
  if (!name || Todos.isSubmitting()) {
    return;
  }
  try {
    await storeValue('todoSubmitting', true, false);
    await POST_Todo.run({
      name
    });
    await resetWidget('NewTodoInput');
    await GET_Todos.run();
  } finally {
    await storeValue('todoSubmitting', false, false);
  }
}

