actionConfiguration.body:
async () => {
  const msg = ReverseInput.text;
  if (!msg || ReverseEcho.isSubmitting()) {
    return;
  }
  try {
    await storeValue('isSubmitting', true, false);
    const response = await GET_ReverseEcho.run({
      msg
    });
    await storeValue('lastMsg', msg);
    await storeValue('lastEcho', response);
  } finally {
    await storeValue('isSubmitting', false, false);
  }
}

