actionConfiguration.body:
async () => {
  const customer = Customers.selectedCustomer();
  const mutable = Customers.mutableConfig();
  const idx = customer?.configs?.findIndex(config => config.databaseName === mutable.databaseName);
  if (idx >= 0) {
    try {
      customer.configs[idx] = mutable;
      await storeValue('isApplying', true, false);
      await Update_Customer.run({
        customerId: customer.id,
        customerInfo: customer
      });
      await storeValue('selectedCustomer', customer, false);
    } finally {
      await storeValue('isApplying', false, false);
    }
  }
}

