actionConfiguration.body:
async () => {
  const customerId = CustomerIdInput.text;
  if (!customerId || Customers.isFinding()) {
    return;
  }
  try {
    await storeValue('isFinding', true, false);
    const response = await Customer_Info.run({
      customerId
    });
    await Customers.initState();
    await Promise.all([storeValue('initDatabaseOption', response.configs[0]?.databaseName, false), storeValue('initLicenseCount', response.configs[0]?.licenseCount, false), storeValue('selectedCustomer', response, false), storeValue('mutableConfigs', response.configs, false)]);
  } finally {
    await storeValue('isFinding', false, false);
  }
}

